sys_app_module.query:
if (!x_snc_userreview.cert_audit_definition) {

//creates the UAR audit definition
var gr = new GlideRecord('cert_audit_definition');
gr.initialize();
gr.name = 'User Access Review';
gr.description = 'This Data Certification Definition is used for the User Access Review Application';
gr.days_to_complete =gs.getProperty(' x_snc_userreview.days_to_complete;');
gr.insert();
gs.setProperty("x_snc_userreview.cert_audit_definition",gr.sys_id);
gs.info('Set x_snc_userreview.cert_audit_definition to '+gr.sys_id);

//used to modify the Module so the newly created UAR certification is displayed
var mod = new GlideRecord('sys_app_module');
mod.addQuery('sys_id','dd9c71514f0df2009457ac118110c744');
mod.query();
while (mod.next()) {
mod.query='sys_id='+gr.sys_id;
mod.update();
}

// used to create the certification schedule
var cs = new GlideRecord('cert_schedule')
cs.name='User Access Review Schedule';
cs.table='x_snc_userreview_access_levels';
cs.display_fields=gs.getProperty('x_snc_userreview.display_fields');
cs.fields=gs.getProperty('x_snc_userreview.fields');
cs.assign_to=gs.getProperty('x_snc_userreview.assign_to');
cs.days_to_complete=gs.getProperty('x_snc_userreview.days_to_complete');
cs.task_description=gs.getProperty('x_snc_userreview.task_description');
cs.instructions=gs.getProperty('x_snc_userreview.instructions');
cs.insert();

var m2m = new GlideRecord('m2m_cert_audit_def_cert_sched');
m2m.cert_audit_definition=gr.sys_id;
m2m.cert_schedule=cs.sys_id;
m2m.insert();

}
